import React, {useState, useEffect} from 'react';
import axios from 'axios'

const Currency = props => {

    const [curr, setCurr] = useState([]);
    const [base, setBase] = useState("USD");
    const [myTarget, setMyTarget] = useState("EUR");
    const [amount, setAmount] = useState();
    const options = ["USD", "EUR", "GBP", "CHF", "CAD", "AUD", "NZD", "JPY"]
    const optionsTwo = ["EUR", "GBP", "CHF", "CAD", "AUD", "NZD", "JPY", "USD"]
    
    useEffect(() => {
        axios.get(`https://v6.exchangerate-api.com/v6/cc2554e33a999147f5554821/pair/${base}/${myTarget}`)
        .then(response => setCurr(response.data))
        .catch(err => console.log("Something went wrong", err))
    }, [base, myTarget])

    const onChangeBase = e => {
        setBase(e.target.value);
    }

    const onChangeTarget = e => {
        setMyTarget(e.target.value);
    }

    const onBaseInput = e => {
        setAmount(e.target.value * curr.conversion_rate);
    }

    return (
        
        <div className="money">
            <h1 style={{fontSize: "40px", marginTop: "70px", marginBottom: "20px"}}>Convert Currency</h1>

            <div>               
                <select onChange={onChangeBase} className="base" name="base" style={{marginRight: "7px"}}>
                    {
                        options.map((item, i) => {
                            return <option value={item} key={i}>{item}</option>
                        })
                    }
                </select>
                <input onChange={onBaseInput} className="in" type="number"/>
            </div>

            <div style={{fontWeight: "bold", fontSize: "30px"}}>=</div>

            <div>               
                <select onChange={onChangeTarget} className="target" name="myTarget" style={{marginRight: "7px"}}>
                    {
                        optionsTwo.map((item, i) => {
                            return <option value={item} key={i}>{item}</option>
                        })
                    }
                </select>
                <input value={amount} className="in" type="number"/>
            </div>

            <table style={{marginTop: "25px"}} className="table table-striped container">
                <thead>
                    <tr>
                    <th scope="col"></th>
                    <th scope="col">Currency</th>
                    <th scope="col">Currency Name</th>
                    <th scope="col">Country</th>
                    </tr>
                </thead>

                <tbody>
                    <tr>
                        <th scope="row"><img style={{height: "25px", width: "40px"}} src="data:image/png;base64," alt="USD"/> </th>
                        <td>USD</td>
                        <td>United States Dollar</td>
                        <td>United States</td>
                    </tr>
                    
                    <tr>
                        <th scope="row"><img style={{height: "25px", width: "40px"}} src="data:image/png;base64," alt="EUR"/></th>
                        <td>EUR</td>
                        <td>Euro</td>
                        <td>European Union</td>
                    </tr>

                    <tr>
                        <th scope="row"><img style={{height: "25px", width: "40px"}} src="data:image/png;base64," alt="GBP"/></th>
                        <td>GBP</td>
                        <td>The British Pound</td>
                        <td>United Kingdom</td>
                    </tr>

                    <tr>
                        <th scope="row"><img style={{height: "25px", width: "40px"}} src="data:image/png;base64," alt="CHF"/></th>
                        <td>CHF</td>
                        <td>Swiss Franc</td>
                        <td>Switzerland</td>
                    </tr>

                    <tr>
                        <th scope="row"><img style={{height: "25px", width: "40px"}} src="data:image/jpeg;base64," alt="CAD"/></th>
                        <td>CAD</td>
                        <td>Canadian Dollar</td>
                        <td>Canada</td>
                    </tr>

                    <tr>
                        <th scope="row"><img style={{height: "25px", width: "40px"}} src="data:image/png;base64," alt="AUD"/></th>
                        <td>AUD</td>
                        <td>Australian Dollar</td>
                        <td>Australia</td>
                    </tr>

                    <tr>
                        <th scope="row"><img style={{height: "25px", width: "40px"}} src="data:image/png;base64," alt="NZD"/></th>
                        <td>NZD</td>
                        <td>New Zealand Dollar</td>
                        <td>New Zealand</td>
                    </tr>

                    <tr>
                        <th scope="row"><img style={{height: "25px", width: "40px"}} src="data:image/png;base64," alt="JPY"/></th>
                        <td>JPY</td>
                        <td>Japanese Yen</td>
                        <td>Japan</td>
                    </tr>

                </tbody>
            </table>

        </div>
    )

}

export default Currency